var Gauge = require('.')

var gt = new Gauge(process.stdout, {updateInterval: 100})
var progress = 0

var pulse = setInterval(function () {
  gt.pulse('interval')
}, 50)
var prog = setInterval(function () {
  gt.show('pro--gress progress progress progress progress progress progress progress', progress += 0.025)
  if (progress >= 1) {
    clearInterval(prog)
    clearInterval(pulse)
    gt.disable()
  }
}, 125)
gt.show({section:'xxx', subsection: ''})
